<%@ Page Language="C#" %>\n\n<%@ Import Namespace="System.Linq" %>\n\n<script runat="server">\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        try\n        {\n            int[] numbers = { 1, 2, 3, 4, 5 };\n            var query = from num in numbers\n                        where num % 2 == 0\n                        select num;\n\n            foreach (var num in query)\n            {\n                Response.Write(num + " ");\n            }\n        }\n        catch (Exception ex)\n        {\n            Response.Write("Error: " + ex.Message);\n        }\n    }\n</script>