using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n\n    using (SqlCommand command = new SqlCommand("MyProcedure", connection))\n    {\n        command.CommandType = CommandType.StoredProcedure;\n\n        // Create a DataTable to pass to the stored procedure\n        DataTable table = new DataTable();\n        table.Columns.Add("Column1", typeof(int));\n        table.Columns.Add("Column2", typeof(string));\n        table.Columns.Add("Column3", typeof(DateTime));\n\n        // Add data to the DataTable\n        // ...\n\n        // Pass the DataTable as a table-valued parameter\n        SqlParameter parameter = new SqlParameter("@MyTableParam", SqlDbType.Structured);\n        parameter.TypeName = "MyTableType";\n        parameter.Value = table;\n\n        command.Parameters.Add(parameter);\n\n        // Execute the stored procedure\n        command.ExecuteNonQuery();\n    }\n}