using System;\nusing System.Runtime.InteropServices;\n\nclass ParallelPortTest\n{\n    [DllImport("inpoutx64.dll", EntryPoint = "Out32")]\n    public static extern void Out32(int address, int value);\n\n    [DllImport("inpoutx64.dll", EntryPoint = "Inp32")]\n    public static extern int Inp32(int address);\n\n    static void Main(string[] args)\n    {\n        int address = 0x378; // default parallel port address\n        int value = 0x00; // value to write to the port\n\n        Out32(address, value);\n        int readValue = Inp32(address);\n\n        Console.WriteLine("Read value: " + readValue);\n    }\n}