public class WebsiteController : Controller\n{\n    public ActionResult Index()\n    {\n        DataSet dataSet = new DataSet();\n        // populate the dataset with data from a database or other source\n\n        WebsiteListModel model = new WebsiteListModel();\n        model.Websites = new List<Website>();\n\n        foreach (DataRow row in dataSet.Tables[0].Rows)\n        {\n            Website website = new Website();\n            website.Id = (int)row["Id"];\n            website.Name = (string)row["Name"];\n            model.Websites.Add(website);\n        }\n\n        return View(model);\n    }\n}