public class DiagnosticController : Controller\n{\n    private readonly DiagnosticContext _diagnosticContext;\n\n    public DiagnosticController(DiagnosticContext diagnosticContext)\n    {\n        _diagnosticContext = diagnosticContext;\n    }\n\n    public IActionResult Index()\n    {\n        // Run diagnostic checks and store results in DiagnosticContext\n        var databaseCheck = DiagnosticCheckFactory.CreateDatabaseConnectionCheck();\n        var filesystemCheck = DiagnosticCheckFactory.CreateFilesystemAccessCheck();\n\n        _diagnosticContext.AddResult(databaseCheck.Execute());\n        _diagnosticContext.AddResult(filesystemCheck.Execute());\n\n        return View(_diagnosticContext.Results);\n    }\n}