using System;\nusing Microsoft.TeamFoundation.VersionControl.Client;\n\npublic class PreventLabelOverwritePolicy : PolicyBase\n{\n    public override string Description\n    {\n        get { return "Prevents overwriting of existing labels"; }\n    }\n\n    public override bool Evaluate()\n    {\n        var pendingChanges = PendingChanges.GetPendingChanges();\n        foreach (var change in pendingChanges.Changes)\n        {\n            if (change.Item.ItemType == ItemType.Label)\n            {\n                var existingLabel = VersionControlServer.GetLabel(change.Item.Name);\n                if (existingLabel != null)\n                {\n                    return false; // Label already exists, block check-in\n                }\n            }\n        }\n        return true; // No existing label found, allow check-in\n    }\n}