using Aspose.Email;\nusing System.IO;\n\n// Load the .eml file from a MemoryStream\nMemoryStream emlStream = new MemoryStream(emlBytes);\nMailMessage message = MailMessage.Load(emlStream);\n\n// Access email message properties\nstring subject = message.Subject;\nstring body = message.Body;\nstring from = message.From.ToString();\n\n// Similarly, you can load .msg files\nMemoryStream msgStream = new MemoryStream(msgBytes);\nMapiMessage msgMessage = MapiMessage.FromStream(msgStream);\n\n// Access email message properties\nstring subject = msgMessage.Subject;\nstring body = msgMessage.Body;\nstring from = msgMessage.SenderEmailAddress;