using System.Configuration;\n\npublic class form : System.Windows.Forms.Form\n{\n    public form()\n    {\n        // This call is required by the Windows Form Designer.\n        InitializeComponent();\n\n        // Open the configuration file manually\n        Configuration config = ConfigurationManager.OpenExeConfiguration("YourDllName.dll");\n\n        // Get the setting value from the configuration file\n        string ipAddress = config.AppSettings.Settings["IPAddress"].Value;\n\n        IP = IPAddress.Parse(ipAddress);\n    }\n}