using OpenCvSharp;\n\n// Load the image\nusing (var img = new Mat("image.png"))\n{\n    // Convert the image to grayscale\n    var gray = new Mat();\n    Cv2.CvtColor(img, gray, ColorConversionCodes.BGR2GRAY);\n\n    // Apply thresholding to segment out the text\n    var thresh = new Mat();\n    Cv2.Threshold(gray, thresh, 0, 255, ThresholdTypes.BinaryInv + ThresholdTypes.Otsu);\n\n    // Recognize the text using Tesseract\n    var text = Tesseract.GetText(thresh);\n    Console.WriteLine(text);\n}