using Newtonsoft.Json;\n\npublic class DefaultValues\n{\n    public string Property1 { get; set; }\n    public int Property2 { get; set; }\n    // ...\n}\n\npublic class MyClass\n{\n    private DefaultValues _defaultValues;\n\n    public MyClass()\n    {\n        LoadDefaultValues();\n    }\n\n    private void LoadDefaultValues()\n    {\n        string json = File.ReadAllText("default_values.json");\n        _defaultValues = JsonConvert.DeserializeObject<DefaultValues>(json);\n    }\n\n    public void SaveDefaultValues()\n    {\n        string json = JsonConvert.SerializeObject(_defaultValues);\n        File.WriteAllText("default_values.json", json);\n    }\n}