public static void Function(int i, string s)\n{\n    if (i <= 0)\n    {\n        throw new ArgumentOutOfRangeException(nameof(i), "Must be greater than 0");\n    }\n    if (s == null)\n    {\n        throw new ArgumentNullException(nameof(s));\n    }\n    if (s.Length == 0)\n    {\n        throw new ArgumentException("Cannot be empty", nameof(s));\n    }\n    // Rest of the function implementation\n}