using System;\nusing System.DirectoryServices;\n\npublic class IISHelper\n{\n    public static void AddScriptMap(string siteName, string extension, string executablePath)\n    {\n        DirectoryEntry site = new DirectoryEntry("IIS://" + siteName + "/Root");\n        site.Properties["ScriptMaps"].Add(extension, executablePath);\n        site.CommitChanges();\n    }\n\n    public static void RemoveScriptMap(string siteName, string extension)\n    {\n        DirectoryEntry site = new DirectoryEntry("IIS://" + siteName + "/Root");\n        site.Properties["ScriptMaps"].Remove(extension);\n        site.CommitChanges();\n    }\n}