protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Initialize the visibility status of the Div tags\n        ViewState["Div1Visible"] = Div1.Visible;\n        ViewState["Div2Visible"] = Div2.Visible;\n        // ...\n    }\n}\n\nprotected void Page_PreRender(object sender, EventArgs e)\n{\n    // Restore the visibility status of the Div tags from ViewState\n    Div1.Visible = (bool)ViewState["Div1Visible"];\n    Div2.Visible = (bool)ViewState["Div2Visible"];\n    // ...\n}\n\n// Whenever you change the visibility of a Div tag, update the ViewState\nprivate void ChangeDivVisibility(Div div, bool visible)\n{\n    div.Visible = visible;\n    ViewState[div.ID + "Visible"] = visible;\n}