using (var db = new YourDataContext())\n{\n    var defaultRegions = db.DefaultRegions;\n    var defaultSubRegions = db.DefaultSubRegions;\n    var defaultCountries = db.DefaultCountries;\n\n    var newGameId = Guid.NewGuid(); // replace with your game ID\n\n    var regions = defaultRegions.Select(r => new Region\n    {\n        GameId = newGameId,\n        Name = r.Name,\n        // other properties...\n    });\n\n    var subRegions = defaultSubRegions.Select(sr => new SubRegion\n    {\n        GameId = newGameId,\n        RegionId = regions.First(r => r.Name == sr.Region.Name).Id,\n        Name = sr.Name,\n        // other properties...\n    });\n\n    var countries = defaultCountries.Select(c => new Country\n    {\n        GameId = newGameId,\n        SubRegionId = subRegions.First(sr => sr.Name == c.SubRegion.Name).Id,\n        Name = c.Name,\n        // other properties...\n    });\n\n    db.Regions.InsertAllOnSubmit(regions);\n    db.SubRegions.InsertAllOnSubmit(subRegions);\n    db.Countries.InsertAllOnSubmit(countries);\n\n    db.SubmitChanges();\n}