using System.ServiceProcess;\n\npublic class MyServiceController\n{\n    public void ReRunImportProcess()\n    {\n        var serviceController = new ServiceController("MyWindowsServiceName");\n        serviceController.ExecuteCommand(128); // custom command to re-run import process\n    }\n}\n\n// In your Windows service\nprotected override void OnCustomCommand(int command)\n{\n    if (command == 128)\n    {\n        // Re-run import process\n        ImportProcess();\n    }\n}