using System;\nusing System.ServiceModel;\n\npublic class MyWcfClient\n{\n    public MyWcfClient()\n    {\n        // Initialize the WCF client\n        var endpointAddress = new EndpointAddress("http://your-service-url.com/MyService");\n        var binding = new BasicHttpBinding();\n        var channelFactory = new ChannelFactory<IMyService>(binding, endpointAddress);\n\n        // Create the client\n        var client = channelFactory.CreateChannel();\n    }\n}