// Group the songs by artist\nDataView dataView = dataSet.Tables["Song"].DefaultView;\ndataView.Sort = "Artist";\n\n// Create groups in the ListView\nforeach (DataRowView row in dataView)\n{\n    string artist = row["Artist"].ToString();\n    ListViewGroup group = listView1.Groups[artist];\n    if (group == null)\n    {\n        group = new ListViewGroup(artist);\n        listView1.Groups.Add(group);\n    }\n\n    // Add the song to the group\n    ListViewItem item = new ListViewItem(row["Name"].ToString());\n    item.Group = group;\n    listView1.Items.Add(item);\n}