using System;\nusing System.Windows.Forms;\nusing System.Xml;\nusing System.Data;\n\npublic partial class Form1 : Form\n{\n    public Form1()\n    {\n        InitializeComponent();\n\n        // Load the XML file into a DataSet\n        DataSet dataSet = new DataSet();\n        dataSet.ReadXml("songs.xml");\n\n        // Create a BindingSource and set its DataSource to the DataSet\n        BindingSource bindingSource = new BindingSource();\n        bindingSource.DataSource = dataSet;\n        bindingSource.DataMember = "Song";\n\n        // Bind the ListView to the BindingSource\n        listView1.DataSource = bindingSource;\n\n        // Set the DisplayMember and ValueMember properties for each column\n        listView1.Columns.Add("Name", 100, HorizontalAlignment.Left);\n        listView1.Columns.Add("Artist", 100, HorizontalAlignment.Left);\n        listView1.Columns.Add("Genre", 100, HorizontalAlignment.Left);\n\n        listView1.DisplayMember = "Name";\n        listView1.ValueMember = "Name";\n    }\n}