// Model\npublic class User\n{\n    public string Name { get; set; }\n    public string Password { get; set; }\n}\n\n// Controller\npublic class UserController\n{\n    private readonly IUserRepository _repository;\n\n    public UserController(IUserRepository repository)\n    {\n        _repository = repository;\n    }\n\n    public void CreateUser()\n    {\n        Console.Write("Enter name: ");\n        var name = Console.ReadLine();\n        Console.Write("Enter password: ");\n        var password = ReadPassword();\n\n        var user = new User { Name = name, Password = password };\n        _repository.SaveUser(user);\n    }\n\n    private string ReadPassword()\n    {\n        var password = string.Empty;\n        while (true)\n        {\n            var keyInfo = Console.ReadKey(true);\n            if (keyInfo.Key == ConsoleKey.Enter)\n            {\n                Console.WriteLine();\n                return password;\n            }\n            else if (keyInfo.Key == ConsoleKey.Backspace)\n            {\n                if (password.Length > 0)\n                {\n                    Console.Write("\b \b"); // Backspace\n                    password = password.Substring(0, password.Length - 1);\n                }\n            }\n            else\n            {\n                Console.Write("*");\n                password += keyInfo.KeyChar;\n            }\n        }\n    }\n}\n\n// View (Console)\npublic class ConsoleView\n{\n    private readonly UserController _controller;\n\n    public ConsoleView(UserController controller)\n    {\n        _controller = controller;\n    }\n\n    public void Run()\n    {\n        Console.WriteLine("Create User");\n        Console.WriteLine("-----------");\n        _controller.CreateUser();\n        Console.WriteLine("User created successfully.");\n    }\n}\n\n// Repository (Mock implementation for demonstration purposes)\npublic interface IUserRepository\n{\n    void SaveUser(User user);\n}\n\npublic class MockUserRepository : IUserRepository\n{\n    public void SaveUser(User user)\n    {\n        Console.WriteLine($"Saving user {user.Name}...");\n    }\n}\n\n// Program\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var repository = new MockUserRepository();\n        var controller = new UserController(repository);\n        var view = new ConsoleView(controller);\n\n        view.Run();\n    }\n}