var sqlStatement = "CREATE TABLE [dbo].[User]( [UserId] [int] IDENTITY(1,1) NOT NULL, [FirstName] [varchar](50) COLLATE SQL_Latin1_General_CP1_CI_AS NOT NULL, [MiddleName] [varchar](50) COLLATE SQL_Latin1_General_CP1_CI_A";\n\nvar pattern = @"\[([^\]]+)\]\s+\[([^\]]+)\](?:\([^\)]+\))?";\nvar matches = Regex.Matches(sqlStatement, pattern);\n\nforeach (Match match in matches)\n{\n    var fieldName = match.Groups[1].Value;\n    var dataType = match.Groups[2].Value;\n    Console.WriteLine($"{fieldName} {dataType}");\n}