using System;\nusing System.Web.UI;\nusing System.Web.UI.WebControls;\n\npublic partial class DynamicForm : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Get the generated class type\n        Type xsdType = Type.GetType("YourSchema");\n\n        // Create an instance of the generated class\n        object xsdInstance = Activator.CreateInstance(xsdType);\n\n        // Iterate over the properties and create form controls\n        foreach (PropertyInfo property in xsdType.GetProperties())\n        {\n            // Create a label and a textbox for each property\n            Label label = new Label { Text = property.Name };\n            TextBox textbox = new TextBox { ID = property.Name };\n\n            // Add the controls to the form\n            form1.Controls.Add(label);\n            form1.Controls.Add(textbox);\n        }\n    }\n}