public class LoginViewModel\n{\n    public bool IsLoginEnabled { get; private set; }\n\n    public string User { get; set; }\n    public string Password { get; set; }\n    public string Server { get; set; }\n\n    public LoginViewModel()\n    {\n        PropertyChanged += (sender, args) =>\n        {\n            if (args.PropertyName == "User" || args.PropertyName == "Password" || args.PropertyName == "Server")\n            {\n                IsLoginEnabled = !string.IsNullOrEmpty(User) && !string.IsNullOrEmpty(Password) && !string.IsNullOrEmpty(Server);\n            }\n        };\n    }\n}