using Microsoft.Win32;\n\nclass Program {\n    static void Main(string[] args) {\n        string subKey = @"SOFTWARE\YourCompany\YourApp";\n        string file = @"C:\path\to\export.reg";\n\n        // Open the key\n        RegistryKey key = Registry.CurrentUser.OpenSubKey(subKey, true);\n\n        // Export the key\n        key.Export(file);\n\n        // Import the key\n        key.Import(file);\n\n        // Close the key\n        key.Close();\n    }\n}