using System.Web.Caching;\nusing SubSonic;\n\n// Assuming you have a SubSonic repository\nvar repo = new MyRepository();\n\n// Create a SqlCacheDependency object\nvar cacheDependency = new SqlCacheDependency(repo.GetProvider().GetConnectionString(), "myTable");\n\n// Get the data from the database using SubSonic\nvar data = repo.Get<MyData>().ToList();\n\n// Cache the data with the dependency\nHttpRuntime.Cache.Insert("myData", data, cacheDependency);