using System;\nusing System.DirectoryServices;\n\nclass InstallSslCert\n{\n    static void Main(string[] args)\n    {\n        string websiteName = "YourWebsite";\n        string certPath = "path/to/cert.pfx";\n        string password = "password";\n\n        // For IIS 7 and later\n        using (var iisManager = new Microsoft.Web.Administration.ServerManager())\n        {\n            var website = iisManager.Sites[websiteName];\n            var binding = website.Bindings.Add("https", 443, certPath, password);\n            iisManager.CommitChanges();\n        }\n\n        // For IIS 6\n        using (var directoryEntry = new DirectoryEntry("IIS://localhost/W3SVC/" + websiteName))\n        {\n            var cert = new System.Security.Cryptography.X509Certificates.X509Certificate2(certPath, password);\n            directoryEntry.Properties["SSLCertificate"].Value = cert.Export(System.Security.Cryptography.X509Certificates.X509ContentType.SerializedCert);\n            directoryEntry.CommitChanges();\n        }\n    }\n}