using Oracle.ManagedDataAccess.Client;\n\n// Create a connection to the Oracle database\nOracleConnection con = new OracleConnection("User Id=<username>;Password=<password>;Data Source=<datasource>");\n\n// Create an OracleCommand object\nOracleCommand cmd = new OracleCommand("BEGIN ... END;", con);\n\n// Set the CommandType to StoredProcedure\ncmd.CommandType = CommandType.StoredProcedure;\n\n// Open the connection and execute the command\ncon.Open();\ncmd.ExecuteNonQuery();\ncon.Close();