using System.Data.OleDb;\n\n// Connection strings\nstring fooDbConnString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=fooDb.mdb";\nstring barDbConnString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=barDb.mdb";\n\n// Create connections\nusing (OleDbConnection fooDbConn = new OleDbConnection(fooDbConnString))\nusing (OleDbConnection barDbConn = new OleDbConnection(barDbConnString))\n{\n    fooDbConn.Open();\n    barDbConn.Open();\n\n    // Update linked table contents\n    string updateQuery = "UPDATE fooTable SET fooField = barField FROM fooTable INNER JOIN barTable ON fooTable.id = barTable.id";\n    OleDbCommand updateCmd = new OleDbCommand(updateQuery, fooDbConn);\n    updateCmd.ExecuteNonQuery();\n}