using System.Data.OleDb;\n\n// Connection strings\nstring fooDbConnString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=fooDb.mdb";\nstring newBarDbConnString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=newBarDb.mdb";\n\n// Create connections\nusing (OleDbConnection fooDbConn = new OleDbConnection(fooDbConnString))\nusing (OleDbConnection newBarDbConn = new OleDbConnection(newBarDbConnString))\n{\n    fooDbConn.Open();\n    newBarDbConn.Open();\n\n    // Get the linked table name and database path\n    string linkedTableName = "fooTable";\n    string databasePath = newBarDbConn.DataSource;\n\n    // Create a new linked table\n    string createLinkedTableQuery = $"CREATE TABLE [{linkedTableName}] (id AUTOINCREMENT PRIMARY KEY, fooField TEXT) IN '{databasePath}'";\n    OleDbCommand createLinkedTableCmd = new OleDbCommand(createLinkedTableQuery, fooDbConn);\n    createLinkedTableCmd.ExecuteNonQuery();\n\n    // Update the linked table connection\n    string updateLinkedTableQuery = $"UPDATE [{linkedTableName}] SET CONNECT = '{newBarDbConnString}'";\n    OleDbCommand updateLinkedTableCmd = new OleDbCommand(updateLinkedTableQuery, fooDbConn);\n    updateLinkedTableCmd.ExecuteNonQuery();\n}