using System;\nusing System.Reflection;\n\nclass PartialTrustChecker\n{\n    static void Main(string[] args)\n    {\n        string[] assemblies = new string[] { "Assembly1.dll", "Assembly2.dll", ... };\n\n        foreach (string assemblyName in assemblies)\n        {\n            Assembly assembly = Assembly.LoadFrom(assemblyName);\n            object[] attributes = assembly.GetCustomAttributes(typeof(AllowPartiallyTrustedCallersAttribute), false);\n\n            if (attributes.Length == 0)\n            {\n                Console.WriteLine($"{assemblyName} does not have AllowPartiallyTrustedCallers attribute");\n            }\n            else\n            {\n                Console.WriteLine($"{assemblyName} has AllowPartiallyTrustedCallers attribute");\n            }\n        }\n    }\n}