using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string[] computers = { "computer1", "computer2", "computer3" };\n\n        foreach (string computer in computers)\n        {\n            ManagementScope scope = new ManagementScope($"\\\\{computer}\\root\\cimv2");\n            ObjectQuery query = new ObjectQuery("SELECT * FROM Win32_Service WHERE Name = 'termservice'");\n            ManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, query);\n            ManagementObjectCollection results = searcher.Get();\n\n            foreach (ManagementObject result in results)\n            {\n                if ((uint)result["State"] == 4) // Running\n                {\n                    Console.WriteLine($"Terminal Services is running on {computer}");\n                }\n                else\n                {\n                    Console.WriteLine($"Terminal Services is not running on {computer}");\n                }\n            }\n        }\n    }\n}