using System;\nusing System.ServiceProcess;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string[] computers = { "computer1", "computer2", "computer3" };\n\n        foreach (string computer in computers)\n        {\n            ServiceController sc = new ServiceController("termservice", computer);\n            if (sc.Status == ServiceControllerStatus.Running)\n            {\n                Console.WriteLine($"Terminal Services is running on {computer}");\n            }\n            else\n            {\n                Console.WriteLine($"Terminal Services is not running on {computer}");\n            }\n        }\n    }\n}