// Web.config\n<system.web>\n  <roleManager enabled="true">\n    <roles>\n      <add name="Admin" />\n      <add name="User" />\n    </roles>\n  </roleManager>\n</system.web>\n\n// Code-behind\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (Page.User.IsInRole("Admin"))\n    {\n        adminControl.Visible = true;\n    }\n    else\n    {\n        adminControl.Visible = false;\n    }\n}