[DllImport("kernel32", SetLastError = true)]\nstatic extern IntPtr OpenProcess(int dwDesiredAccess, bool bInheritHandle, int dwProcessId);\n\nconst int PROCESS_QUERY_INFORMATION = 0x0400;\n\n// ...\n\nIntPtr hProcess = OpenProcess(PROCESS_QUERY_INFORMATION, false, Process.GetCurrentProcess().Id);\nif (hProcess != IntPtr.Zero)\n{\n    // You can now use the hProcess handle to query the process information\n    // Don't forget to close the handle when you're done:\n    CloseHandle(hProcess);\n}