using SourceSafeTypeLib;\n\nclass VSSExample\n{\n    public static void Main(string[] args)\n    {\n        // Create a new VSS database instance\n        IVSSDatabase db = new VSSDatabase();\n\n        // Open the VSS database\n        db.Open(@"C:\Path\To\VSS\Database", "username", "password");\n\n        // Get the latest version of a file\n        IVSSItem item = db.get_VSSItem("$/Path/To/File.txt", VSSItemType.VSSITEM_FILE);\n        item.GetLatest();\n\n        // Undo another user's checkout\n        IVSSCheckout checkout = item.get_Checkout("otheruser");\n        checkout.Undo();\n\n        // Determine who has a file checked out\n        IVSSCheckout[] checkouts = item.get_Checkouts();\n        foreach (IVSSCheckout checkout in checkouts)\n        {\n            Console.WriteLine($"File checked out by: {checkout.Username}");\n        }\n    }\n}