// Create a texture to render the text to\nuint textureId;\nGl.glGenTextures(1, out textureId);\nGl.glBindTexture(Gl.GL_TEXTURE_2D, textureId);\n\n// Render the text to the texture\nfuenteActual.ftBeginFont();\nfuenteActual.ftWrite(texto.Text, texto.Alineacion == Align.left ? FTFontAlign.FT_ALIGN_LEFT : texto.Alineacion == Align.center ? FTFontAlign.FT_ALIGN_CENTERED : FTFontAlign.FT_ALIGN_RIGHT);\nfuenteActual.ftEndFont();\n\n// Render the texture as a quad with the desired rotation\nGl.glBindTexture(Gl.GL_TEXTURE_2D, textureId);\nGl.glEnable(Gl.GL_TEXTURE_2D);\n\nGl.glBegin(Gl.GL_QUADS);\nGl.glTexCoord2f(0, 0); Gl.glVertex2f(-10, -10);\nGl.glTexCoord2f(1, 0); Gl.glVertex2f(10, -10);\nGl.glTexCoord2f(1, 1); Gl.glVertex2f(10, 10);\nGl.glTexCoord2f(0, 1); Gl.glVertex2f(-10, 10);\nGl.glEnd();\n\nGl.glDisable(Gl.GL_TEXTURE_2D);