// When creating the font\nfuenteActual = new FTFont(fuentes.FontMap[fuente], out ultimoError);\nfuenteActual.ftRenderToTexture(64, 96);\nfuenteActual.FT_ALIGN = FTFontAlign.FT_ALIGN_LEFT; // Set default alignment to left\n\n// When rendering the text\nfuenteActual.ftBeginFont();\nfuenteActual.ftWrite(texto.Text, texto.Alineacion == Align.left ? FTFontAlign.FT_ALIGN_LEFT : texto.Alineacion == Align.center ? FTFontAlign.FT_ALIGN_CENTERED : FTFontAlign.FT_ALIGN_RIGHT);\nfuenteActual.ftEndFont();