[HttpPost]\npublic ActionResult Register(RegisterModel model)\n{\n    // Generate a cryptographically secure token\n    var token = Guid.NewGuid().ToString("N");\n\n    // Store the token in the user's account\n    var user = new User { Email = model.Email, VerificationToken = token };\n\n    // Send the verification email\n    var verificationUrl = Url.Action("VerifyEmail", "Account", new { token }, Request.Url.Scheme);\n    var emailBody = $"Please verify your email address by clicking this link: {verificationUrl}";\n    // Send the email using your email service\n\n    return RedirectToAction("Index", "Home");\n}\n\n[HttpGet]\npublic ActionResult VerifyEmail(string token)\n{\n    var user = _userRepository.FindByVerificationToken(token);\n    if (user != null)\n    {\n        user.Verified = true;\n        _userRepository.Save(user);\n        return RedirectToAction("Index", "Home");\n    }\n    return HttpNotFound();\n}