using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class TripleDesDecryptor\n{\n    public static string Decrypt(string encryptedString, string key)\n    {\n        byte[] keyBytes = Encoding.UTF8.GetBytes(key);\n        byte[] iv = new byte[8];\n        byte[] input = Convert.FromBase64String(encryptedString);\n\n        using (TripleDESCryptoServiceProvider tripleDes = new TripleDESCryptoServiceProvider())\n        {\n            tripleDes.Key = keyBytes;\n            tripleDes.IV = iv;\n            tripleDes.Mode = CipherMode.CBC;\n            tripleDes.Padding = PaddingMode.Zeros;\n\n            ICryptoTransform decrypt = tripleDes.CreateDecryptor();\n            byte[] result = decrypt.TransformFinalBlock(input, 0, input.Length);\n\n            return Encoding.UTF8.GetString(result).TrimEnd('\x00');\n        }\n    }\n}