// C++/CLI wrapper\npublic ref class MyManagedObject\n{\npublic:\n    MyManagedObject() : nativeObj_(new MyNativeObject()) {}\n    ~MyManagedObject() { delete nativeObj_; }\n\n    property int MyProperty\n    {\n        int get() { return nativeObj_->GetMyProperty(); }\n        void set(int value) { nativeObj_->SetMyProperty(value); }\n    }\n\nprivate:\n    MyNativeObject* nativeObj_;\n};\n\n// C# usage\npublic class MyCSharpObject\n{\n    private MyManagedObject managedObj_;\n\n    public MyCSharpObject() { managedObj_ = new MyManagedObject(); }\n\n    public int MyProperty\n    {\n        get { return managedObj_->MyProperty; }\n        set { managedObj_->MyProperty = value; }\n    }\n}