using System.IdentityModel;\nusing System.Security.Principal;\nusing System.ServiceModel;\n\n// ...\n\nWindowsIdentity clientIdentity = Thread.CurrentPrincipal.Identity as WindowsIdentity;\nif (clientIdentity != null)\n{\n    using (clientIdentity.Impersonate())\n    {\n        // Create the channel factory after impersonation\n        ChannelFactory<YourServiceContract> channelFactory = new ChannelFactory<YourServiceContract>("YourClientEndpoint");\n        YourServiceContract proxy = channelFactory.CreateChannel();\n\n        // Make the WCF call\n        proxy.YourMethodCall();\n    }\n}