// Enable the Developer Dashboard\nSPDeveloperDashboardSettings settings = SPDeveloperDashboardSettings.GetSettings(SPContext.Current.Site);\nsettings.EnableDashboard = true;\nsettings.Update();\n\n// Get the dashboard data\nSPDeveloperDashboardData data = SPDeveloperDashboardData.GetDashboardData(SPContext.Current.Site);\n\n// Analyze the data\nforeach (SPDeveloperDashboardPage page in data.Pages)\n{\n    if (page.ResponseTime > 1000) // 1 second\n    {\n        // Investigate the slow page\n        Console.WriteLine($"Slow page: {page.Url} ({page.ResponseTime}ms)");\n    }\n}