// In the executable's config file (e.g., app.config)\n<configuration>\n    <appSettings>\n        <add key="DatabaseConnectionString" value="..." />\n    </appSettings>\n</configuration>\n\n// In the executable's code\nusing System.Configuration;\n\npublic class MyExecutable\n{\n    public void Initialize()\n    {\n        string dbConnectionString = ConfigurationManager.AppSettings["DatabaseConnectionString"];\n        MyDll myDll = new MyDll(dbConnectionString);\n        // ...\n    }\n}\n\n// In the DLL's code\npublic class MyDll\n{\n    private string _dbConnectionString;\n\n    public MyDll(string dbConnectionString)\n    {\n        _dbConnectionString = dbConnectionString;\n    }\n\n    // Use the dbConnectionString as needed\n}