// Assuming 'payments.dtPayments' is your DataTable\n\n// Create a temporary list to hold rows to be deleted\nList<DataRow> rowsToDelete = new List<DataRow>();\n\nforeach (DataRow row in payments.dtPayments.Rows)\n{\n    // Apply your conditions here\n    if (thisOption == true || (otherOption == true && LastOption == false))\n    {\n        // Mark row for deletion\n        rowsToDelete.Add(row);\n    }\n    else\n    {\n        // Update row if necessary\n        if (otherOption == true)\n        {\n            row["Date"] = myDataReader["date"].ToString().Trim();\n            row["Pay"] = payTypeName(myDataReader["ccdsrc"].ToString().Trim());\n        }\n    }\n}\n\n// Delete marked rows\nforeach (DataRow row in rowsToDelete)\n{\n    row.Delete();\n}\n\n// Accept changes to persist the deletions\npayments.dtPayments.AcceptChanges();