using System.Reflection;\n\n// Load the API's assembly\nAssembly apiAssembly = Assembly.Load("ApiAssembly");\n\n// Get the types in the assembly\nType[] types = apiAssembly.GetTypes();\n\n// Iterate through the types and inspect their members\nforeach (Type type in types)\n{\n    MethodInfo[] methods = type.GetMethods();\n    foreach (MethodInfo method in methods)\n    {\n        Console.WriteLine(method.Name);\n        Console.WriteLine(method.ReturnType);\n        ParameterInfo[] parameters = method.GetParameters();\n        foreach (ParameterInfo parameter in parameters)\n        {\n            Console.WriteLine(parameter.Name);\n            Console.WriteLine(parameter.ParameterType);\n        }\n    }\n}