// Generate token when JavaScript is requested\n[HttpGet]\npublic string GetJavaScript()\n{\n    string token = Guid.NewGuid().ToString();\n    // Store token in session or cache\n    Session["Token"] = token;\n    // Return JavaScript code with token\n    return $"var token = '{token}';";\n}\n\n// Validate token on JSON Web Service request\n[HttpPost]\npublic JsonResult MyJsonService(string token)\n{\n    if (token != (string)Session["Token"])\n    {\n        // Invalid token, return error or throw exception\n        return Json(new { Error = "Invalid token" });\n    }\n    // Token is valid, process request and return JSON\n    // ...\n}