var result = from a in context.Appointments\n             join b in context.AppointmentFormulas on a.AppointmentId equals b.AppointmentId into ab\n             from b in ab.DefaultIfEmpty()\n             join d in context.Types on a.Type equals d.TypeId\n             join e in context.AppointmentForms on a.AppointmentId equals e.AppointmentId into ae\n             from e in ae.DefaultIfEmpty()\n             where a.RowStatus == 1 && a.Type == 1\n             orderby a.Type\n             select new\n             {\n                 a.AppointmentId,\n                 a.Status,\n                 a.Type,\n                 a.Title,\n                 Days = b != null ? b.Days : 0,\n                 d.Description,\n                 FormId = e != null ? e.FormId : 0\n             };