public static class SqlQueryExtensions\n{\n    public static string ToPlainSql(this SqlQuery qry)\n    {\n        string sql = qry.BuildSqlStatement();\n        foreach (var param in qry.Parameters)\n        {\n            sql = sql.Replace("?" + param.ParameterName, \n                              "'" + param.Value.ToString().Replace("'", "''") + "'");\n        }\n        return sql;\n    }\n}