[ServiceContract]\npublic interface ICustomerService\n{\n    [WebGet(UriTemplate = "/customers/?name={name}")]\n    void GetCustomer(string name);\n}\n\npublic class CustomerService : ICustomerService\n{\n    public void GetCustomer(string name)\n    {\n        // Find the customer by name and get their ID\n        int customerId = FindCustomerByName(name);\n\n        // Set the HTTP status code to 301 (Moved Permanently)\n        WebOperationContext.Current.OutgoingResponse.StatusCode = HttpStatusCode.MovedPermanently;\n\n        // Set the Location header to the new URL\n        WebOperationContext.Current.OutgoingResponse.Headers["Location"] = string.Format("/customers/{0}", customerId);\n    }\n\n    private int FindCustomerByName(string name)\n    {\n        // TO DO: implement logic to find customer by name\n    }\n}