// MyJsonEndpoint.aspx.cs\nusing System;\nusing System.Web;\nusing System.Web.Script.Serialization;\n\npublic partial class MyJsonEndpoint : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Only allow POST requests\n        if (Request.HttpMethod != "POST")\n        {\n            Response.StatusCode = 405; // Method Not Allowed\n            Response.End();\n            return;\n        }\n\n        // Get the posted data\n        string jsonData = new StreamReader(Request.InputStream).ReadToEnd();\n\n        // Process the data (e.g., save to database, perform calculations, etc.)\n        // ...\n\n        // Create a JSON response\n        var serializer = new JavaScriptSerializer();\n        var responseData = new { message = "Hello, World!" };\n        string jsonResponse = serializer.Serialize(responseData);\n\n        // Set the response headers and content\n        Response.ContentType = "application/json";\n        Response.Write(jsonResponse);\n    }\n}