public enum ErrorCode\n{\n    [Description("whatever error")]\n    A01,\n    [Description("another error")]\n    U01,\n    [Description("yet another error type")]\n    U02\n}\n\npublic string GetErrorMessage(string code)\n{\n    ErrorCode errorCode;\n    if (Enum.TryParse(code, out errorCode))\n    {\n        return errorCode.GetDescription();\n    }\n    return null;\n}\n\npublic static string GetDescription(this Enum value)\n{\n    var fieldInfo = value.GetType().GetField(value.ToString());\n    var descriptionAttribute = (DescriptionAttribute)fieldInfo.GetCustomAttributes(typeof(DescriptionAttribute), false).FirstOrDefault();\n    return descriptionAttribute.Description;\n}